def scrape_article(url):
    chrome_options = Options()
    chrome_options.add_argument("--no-sandbox")
    chrome_options.add_argument("--headless")
    chrome_options.add_argument("--disable-gpu")
    chrome_options.add_argument("--disable-dev-shm-usage")
    chrome_options.add_argument('--ignore-certificate-errors-spki-list')
    chrome_options.add_argument('--allow-running-insecure-content')
    chrome_options.add_argument('--disable-web-security')

    # Use WebDriverManager to automatically download the appropriate WebDriver binary
    driver = webdriver.Chrome(options=chrome_options)
    driver.get(url)
    page_source = driver.page_source

    soup = BeautifulSoup(page_source, 'html.parser')

    title = soup.find('h1').text.strip()
    source = soup.find('div', class_='article__credits-author-pub').text.strip()
    published = soup.find('div', class_='article__date-published').text.strip()

    article_body_elements = soup.find('div', class_='article__writeup')

    article_text = ""
    for p in article_body_elements.find_all('p'):
        for strong in p.find_all('strong'):
            strong.decompose()
        for a in p.find_all('a'):
            a.decompose()
        for span in p.find_all('span'):
            span.decompose()
        article_text += p.get_text(strip=True) + "\n\n"

    article_text = article_text.strip()

    driver.quit()
    return title, source, article_text, published


def save_to_file(data, filename):
    with open(filename, 'w', encoding='utf-8') as file:
        for item in data:
            title, source, article, published = item
            file.write(f"{title}\n\n")
            file.write(f"{source}\n\n")
            file.write(f"{published}\n\n")
            file.write(f"{article}\n\n")
            file.write("---\n\n")
            
def save_checkpoint(scraped_data, processed_urls, failed_urls):
    checkpoint_data = {
        'scraped_data': scraped_data,
        'processed_urls': processed_urls,
        'failed_urls': failed_urls
    }
    with open('checkpointp.json', 'w') as checkpoint_file:
        json.dump(checkpoint_data, checkpoint_file)

def load_checkpoint():
    if os.path.exists('checkpointp.json'):
        with open('checkpointp.json', 'r') as checkpoint_file:
            checkpoint_data = json.load(checkpoint_file)
            return checkpoint_data['scraped_data'], checkpoint_data['processed_urls'], checkpoint_data['failed_urls']
    return [], [], []


